(378, '_transient_woocommerce_addons_data', 'O:8:"stdClass":10:{s:8:"featured";a:4:{i:0;O:8:"stdClass":5:{s:5:"title";s:20:"WooCommerce Bookings";s:5:"image";s:0:"";s:7:"excerpt";s:108:"Sell your time or date based bookings! Perfect for those wanting to offer appointments, services or rentals.";s:4:"link";s:115:"https://www.woothemes.com/products/woocommerce-bookings/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;149.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-ons";s:5:"image";s:0:"";s:7:"excerpt";s:110:"Allow your customers to customise your products by adding input boxes, dropdowns or a field set of checkboxes.";s:4:"link";s:110:"https://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:25:"WooCommerce Subscriptions";s:5:"image";s:84:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2012/09/woosubs.png";s:7:"excerpt";s:85:"WC Subscriptions makes it easy to create and manage products with recurring payments.";s:4:"link";s:120:"https://www.woothemes.com/products/woocommerce-subscriptions/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:19:"Table Rate Shipping";s:5:"image";s:0:"";s:7:"excerpt";s:123:"Advanced, flexible shipping. Define multiple shipping rates based on location, price, weight, shipping class or item count.";s:4:"link";s:116:"https://www.woothemes.com/products/table-rate-shipping-2/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}}s:7:"popular";a:24:{i:0;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-ons";s:5:"image";s:0:"";s:7:"excerpt";s:110:"Allow your customers to customise your products by adding input boxes, dropdowns or a field set of checkboxes.";s:4:"link";s:110:"https://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:25:"WooCommerce Subscriptions";s:5:"image";s:84:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2012/09/woosubs.png";s:7:"excerpt";s:85:"WC Subscriptions makes it easy to create and manage products with recurring payments.";s:4:"link";s:120:"https://www.woothemes.com/products/woocommerce-subscriptions/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:19:"Table Rate Shipping";s:5:"image";s:0:"";s:7:"excerpt";s:123:"Advanced, flexible shipping. Define multiple shipping rates based on location, price, weight, shipping class or item count.";s:4:"link";s:116:"https://www.woothemes.com/products/table-rate-shipping-2/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:20:"USPS Shipping Method";s:5:"image";s:61:"https://www.woothemes.com/wp-content/uploads/2012/09/usps.jpg";s:7:"excerpt";s:91:"Get shipping rates from the USPS API which handles both domestic and international parcels.";s:4:"link";s:115:"https://www.woothemes.com/products/usps-shipping-method/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:4;O:8:"stdClass":5:{s:5:"title";s:24:"Amazon Payments Advanced";s:5:"image";s:82:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2012/08/home.jpeg";s:7:"excerpt";s:162:"Amazon Payments Advanced is embedded directly into your WooCommerce store. Buyer interactions take place in embedded widgets, so the buyer never leaves your site.";s:4:"link";s:119:"https://www.woothemes.com/products/amazon-payments-advanced/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}i:5;O:8:"stdClass":5:{s:5:"title";s:24:"Product CSV Import Suite";s:5:"image";s:0:"";s:7:"excerpt";s:91:"Import, merge, and export products and variations to and from WooCommerce using a CSV file.";s:4:"link";s:119:"https://www.woothemes.com/products/product-csv-import-suite/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:6;O:8:"stdClass":5:{s:5:"title";s:19:"UPS Shipping Method";s:5:"image";s:85:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2013/01/ups_logo.gif";s:7:"excerpt";s:90:"Get shipping rates from the UPS API which handles both domestic and international parcels.";s:4:"link";s:114:"https://www.woothemes.com/products/ups-shipping-method/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:7;O:8:"stdClass":5:{s:5:"title";s:15:"Dynamic Pricing";s:5:"image";s:0:"";s:7:"excerpt";s:48:"Bulk discounts, role-based pricing and much more";s:4:"link";s:110:"https://www.woothemes.com/products/dynamic-pricing/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;129.00";}i:8;O:8:"stdClass":5:{s:5:"title";s:35:"Print Invoices &#038; Packing lists";s:5:"image";s:0:"";s:7:"excerpt";s:49:"Print your WooCommerce invoices and packing lists";s:4:"link";s:123:"https://www.woothemes.com/products/print-invoices-packing-lists/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:9;O:8:"stdClass":5:{s:5:"title";s:6:"Stripe";s:5:"image";s:63:"https://www.woothemes.com/wp-content/uploads/2012/09/stripe.png";s:7:"excerpt";s:30:"Stripe gateway for WooCommerce";s:4:"link";s:101:"https://www.woothemes.com/products/stripe/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:10;O:8:"stdClass":5:{s:5:"title";s:25:"Order/Customer CSV Export";s:5:"image";s:0:"";s:7:"excerpt";s:69:"Export orders and customers from WooCommerce to a CSV file with ease.";s:4:"link";s:119:"https://www.woothemes.com/products/ordercustomer-csv-export/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i: